import { render } from '@testing-library/react'
import React from 'react'


class General extends React.Component{
    constructor(props){
    super(props)
        this.state = {
            name: '',
            email: '',
            phone: ''
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleEdit = this.handleEdit.bind(this)


    }

    handleChange(event,n){
        if(n==1){        
        this.setState({name: event.target.value})
        }else if(n==2){
            this.setState({email: event.target.value})
        }else if(n==3){
            this.setState({phone: event.target.value})
        }    
    }
    handleSubmit(event){
        console.log(this.state)
        event.preventDefault();
    }
    handleEdit(event){
        console.log('edit works')
    }
    render(){
        return (
            <form onSubmit={this.handleClick}>
                <label>Name</label>
                <input id='name' type='text' onChange={this.handleChange(1)}/>
                <input id='email' type='text' onChange={this.handleChange(2)}/>
                <input id='phone' type='text' onChange={this.handleChange(3)}/>

                <button type='submit' className='submitBtn' onClick={this.handleSubmit}>Submit</button>
            </form>
        )
    }
}
export default General;
