import { render } from '@testing-library/react'
import React from 'react'


class General extends React.Component{
    constructor(props){
    super(props)
        this.state = {
            name: '',
            email: '',
            phone: ''
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.setName = this.setName.bind(this)
        this.setEmail = this.setEmail.bind(this)
        this.setPhone = this.setPhone.bind(this)


    }

    handleSubmit(event){
        console.log(this.state)
        event.preventDefault();
    }
    handleEdit(event){
        console.log('edit works')
    }
    setName(event){
        this.setState({name: event.target.value})
    }
    setEmail(event){
        this.setState({email: event.target.value})
    }
    setPhone(event){
        this.setState({phone: event.target.value})
    }
    onTrigger = () =>{
        this.props.parentCallback(this.state)
    }
    render(){
        if(this.state.mode==="edit"){
        return (
            <form className="section" onSubmit={this.handleSubmit}>
                <label>Full name</label>
                <input id='name' type='text' onChange={this.setName} value={this.state.name} />
                <label>Email address</label>
                <input id='email' type='text' onChange={this.setEmail} value={this.state.email} />
                <label>Phone number</label>
                <input id='phone' type='text' onChange={this.setPhone} value={this.state.phone} />

                <button type='submit' className='submitBtn btn' onClick={this.handleSubmit} onClick={this.onTrigger}>Submit</button>
            </form>
        )}else if(this.state.mode==="view"){
            return (
                <h1>View Mode</h1>
            )
        }
    }
}
export default General;
