import { render } from '@testing-library/react'
import React from 'react'


class General extends React.Component{
    constructor(props){
    super(props)
        this.state = {
            name: '',
            email: '',
            phone: ''
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.setName = this.setName.bind(this)
        this.setEmail = this.setEmail.bind(this)
        this.setPhone = this.setPhone.bind(this)


    }

    handleChange(event){
        this.setName(event)
        this.setEmail(event)
        this.setPhone(event)
    }
    handleSubmit(event){
        console.log(this.state)
        event.preventDefault();
    }
    handleEdit(event){
        console.log('edit works')
    }
    render(){
        return (
            <form onSubmit={this.handleClick}>
                <label>Name</label>
                <input id='name' type='text' onChange={this.handleChange} value={this.state.name} />
                <input id='email' type='text' onChange={this.handleChange} value={this.state.email} />
                <input id='phone' type='text' onChange={this.handleChange} value={this.state.phone} />

                <button type='submit' className='submitBtn' onClick={this.handleSubmit}>Submit</button>
            </form>
        )
    }
}
export default General;
