import React from 'react';


class Education extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            schoolName: '',
            titleOfStudy: '',
            dateOfStudy: '',

        }
        this.setName = this.setName.bind(this)
        this.setTitle = this.setTitle.bind(this)
        this.setDate = this.setDate.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    handleSubmit(event){
        event.preventDefault();
        console.log(this.state)
    }
    setTitle(event){
        this.setState({titleOfStudy: event.target.value})
    }
    setDate(event){
        this.setState({dateOfStudy: event.target.value})
    }
    setName(event){
        this.setState({schoolName: event.target.value})
    }
    onTrigger = () =>{
        this.props.parentCallback(this.state)
    }
    render(){
        return (
            <form className='section' onSubmit={this.handleSubmit}>
                <label>School name</label>
                <input id='schoolName' type='text' onChange={this.setName} />
                <label>Title of study</label>
                <input id='schoolName' type='text' onChange={this.setTitle} />
                <label>Date of study</label>
                <input id='schoolName' type='text' onChange={this.setDate} />
                <button type='submit' className='btn' onClick={this.handleSubmit} onClick={this.onTrigger}>Submit</button>

            </form>
        )
    }
}
export default Education;
